mapper.select:
and course_title like concat('%', #{courseTitle}, '%')
and
FIND_IN_SET( '${item}', c.`subject`)
and
FIND_IN_SET( '${item}', c.speciality)
and period = #{period}
and
FIND_IN_SET( '${item}', c.learning_scope)
and course_type = #{courseType}
and c.del_flag = #{delFlag}
and IF ( eqb.clazz_id IS NULL, 0, 1 ) = #{questionBank}
and IF ( c.exam_issue IS NULL, 0, c.exam_issue ) = #{questions}
ORDER BY update_time DESC

SELECT DISTINCT u.id_card AS idCard, c.id, c.course_title AS courseTitle, u.id AS userId, u.nickname AS userName, u.sex, u.mobile, a1.area_name_division AS division, u.department_title AS deptName, ( SELECT MIN( start_time ) FROM edu_learning_record_file WHERE course_id = blrf.course_id AND user_id = blrf.user_id ) AS time, blr.study_type AS studyType, IF ( ( SELECT grade FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ) IS NULL, -1, 0 ) AS test, round( ( SELECT grade FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ), 2 ) AS grade, IF ( ( SELECT is_pass FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ) IS NULL, 0, ( SELECT is_pass FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ) ) AS isPass, IF ( ( SELECT id FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ) IS NULL, '', ( SELECT id FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ) ) AS examId FROM edu_course c LEFT JOIN edu_learning_record blr ON blr.course_id = c.id LEFT JOIN edu_learning_record_file blrf ON blrf.course_id = c.id AND blrf.course_id = blr.course_id AND blr.user_id = blrf.user_id LEFT JOIN legion.t_user u ON u.id = blr.user_id LEFT JOIN legion.t_city a1 ON a1.light_path = u.address_ids
u.type = #{stuVideoCourse.userType}
and blr.study_type = #{stuVideoCourse.studyType}
and c.course_title like concat('%', #{stuVideoCourse.courseTitle}, '%')
and c.course_type = #{stuVideoCourse.courseType}
and c.id = #{stuVideoCourse.id}
and u.nickname like concat('%', #{stuVideoCourse.userName}, '%')
AND u.address_ids = #{stuVideoCourse.province}
and
FIND_IN_SET( '${item}', IF ( ( SELECT grade FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ) IS NULL, -1, 0 ))
and
FIND_IN_SET( '${item}', IF ( ( SELECT is_pass FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ) IS NULL, -1, ( SELECT is_pass FROM edu_exam_paper WHERE clazz_id = c.id AND user_id = u.id AND type = 1 ORDER BY create_time DESC LIMIT 1 ) ))
ORDER BY `time` DESC

SELECT DISTINCT u.id_card AS idCard, c.id, c.course_title AS courseTitle, u.id AS userId, u.nickname AS userName, u.sex, u.mobile, a1.area_name AS division, u.department_title AS deptName, ( SELECT MIN( start_time ) FROM edu_learning_record_file WHERE course_id = blrf.course_id AND user_id = blrf.user_id ) AS time, ( SELECT `time` FROM edu_learning_record_file WHERE course_id = blrf.course_id AND user_id = blrf.user_id LIMIT 1 ) AS pdfTime, blr.study_type AS studyType FROM edu_course c LEFT JOIN edu_learning_record blr ON blr.course_id = c.id LEFT JOIN edu_learning_record_file blrf ON blrf.course_id = c.id AND blrf.course_id = blr.course_id AND blr.user_id = blrf.user_id LEFT JOIN legion.t_user u ON u.id = blr.user_id LEFT JOIN legion.t_city a1 ON a1.id = u.address_ids
u.type = concat('0', #{stuPdfCourse.userType}) and blr.study_type = 2
and c.course_type = #{stuPdfCourse.courseType}
and c.id = #{stuPdfCourse.id}
and u.username like concat('%', #{stuPdfCourse.userName}, '%')
AND u.address_ids = #{stuPdfCourse.province}
AND u.department_title like concat('%', #{stuPdfCourse.deptName}, '%')
AND c.course_title like concat('%', #{stuPdfCourse.courseTitle}, '%')
ORDER BY `time` DESC
