mapper.select:
select beq.id, beq.type as 'type', beq.title as 'title', beq.options as 'options', beq.answers as 'answers', beq.clazz_id as 'clazzId', beq.create_time as 'createTime', beq.create_user as 'createUser', beq.analysis as 'analysis', beq.major_id as 'majorId' from edu_exam_question beq
beq.del_flag = '0'
and beq.clazz_id = #{exam.clazzId}
and beq.bank_id = #{exam.bankId}
and beq.type = #{exam.type}
and beq.title like concat('%', #{exam.title}, '%')
and beq.major_id = #{exam.majorId}
order by beq.create_time desc

select
id
from
edu_exam_question
where
clazz_id = #{clazzId}
and
title = #{title}
and
type = #{type}

select
id
from
edu_exam_question
where
bank_id = #{bankId}
and
title = #{title}
and
type = #{type}

select count(*) from edu_exam_question where major_id in
#{id}
and type = #{type} and is_delete = '0'

mapper.update:
update
edu_exam_question_bank
set
create_time = #{date}
where
id = #{id}

update
edu_exam_question_bank
set
create_time = #{time}
where
id = #{bankId}

update
edu_exam_question_bank
set
create_time = #{time}
where
clazz_id = #{clazzId}
